import React, { useDeferredValue, useEffect, useState } from 'react';
import './Box.css';

const Box = ({boxClicked, turnColorGrey}) => {


    const [isClicked, setIsClicked] = useState(false);

    console.log("turn color grey: ", turnColorGrey, isClicked)

    const changeColor = event => {
        setIsClicked(!isClicked)
        boxClicked(event)
    }

    // useEffect(() => {
    //     if (turnColorGrey) {
    //         setTimeout(() => {
    //         setIsClicked(!isClicked)
    //       }, 1000); }
    // }, [turnColorGrey, isClicked])

    return (
        <div className={`box ${!isClicked ? 'bg-grey': 'bg-green'}`} onClick={() => changeColor()}></div>
    )
}

export default Box;
