import React, { useEffect, useState } from 'react';
import './App.css';
import Box from './Box';

const App = () => {

  const [boxClicked, setBoxClicked] = useState(false);

  const numOfBoxes = 7;

  const [numOfBoxesClicked, setNumOfBoxesClicked] = useState(0);

  const [allBoxesClicked, setAllBoxesClicked] = useState(false);

  const incrementCounter = () => {
    console.log("event")
    setNumOfBoxesClicked(numOfBoxesClicked + 1);
  }

  useEffect(() => {
    if (numOfBoxesClicked === numOfBoxes) {
      setAllBoxesClicked(true)
    }
  }, [numOfBoxesClicked])

  return (
    <div>
      <div className='first-div flex wrapper-div'>
        <Box boxClicked={incrementCounter} turnColorGrey={allBoxesClicked}/>
        <Box boxClicked={incrementCounter} turnColorGrey={allBoxesClicked}/>
        <Box boxClicked={incrementCounter} turnColorGrey={allBoxesClicked}/>
      </div>
      <div className='second-div flex wrapper-div'>
        <Box boxClicked={incrementCounter} turnColorGrey={allBoxesClicked}/>
      </div>
      <div className='third-div flex wrapper-div'>
        <Box boxClicked={incrementCounter} turnColorGrey={allBoxesClicked}/>
        <Box boxClicked={incrementCounter} turnColorGrey={allBoxesClicked}/>
        <Box boxClicked={incrementCounter} turnColorGrey={allBoxesClicked}/>
      </div>
    </div>
  )
}

export default App;
