import React, { useEffect, useState } from 'react';
import './Box.css';

const Box = ({boxClicked, turnColorGrey}) => {


    const [isClicked, setIsClicked] = useState(false);

    console.log("turn color grey: ", turnColorGrey, isClicked)

    const changeColor = event => {
        setIsClicked(!isClicked)
        boxClicked(event)
    }

    useEffect(() => {
        if (turnColorGrey) {setIsClicked(!isClicked)}
    }, [turnColorGrey, isClicked])

    return (
        <div className={`box ${!isClicked ? 'bg-grey': 'bg-green'} ${!turnColorGrey ? 'bg-grey' : 'bg-green'}`} onClick={() => changeColor()}></div>
    )
}

export default Box;
